package hospital.management.system;import net.proteanit.sql.DbUtils;import javax.swing.*;import java.awt.*;import java.sql.ResultSet;public class Department extends JFrame {    Department() {        JPanel panel = new JPanel();        panel.setBounds(5, 5, 690, 490);        panel.setLayout(null);        panel.setBackground(new Color(90, 156, 163));        add(panel);        JTable table = new JTable();        table.setRowHeight(25);        table.setFont(new Font("Tahoma", Font.PLAIN, 13));        JScrollPane scrollPane = new JScrollPane(table);        scrollPane.setBounds(0, 40, 690, 350);        panel.add(scrollPane);        try {            conn c = new conn();            String q = "select * from department";            ResultSet resultSet = c.statement.executeQuery(q);            table.setModel(DbUtils.resultSetToTableModel(resultSet));            table.getTableHeader().setFont(new Font("Tahoma", Font.BOLD, 14));            table.getTableHeader().setBackground(new Color(70, 130, 180));            table.getTableHeader().setForeground(Color.WHITE);        } catch (Exception e) {            e.printStackTrace();        }        JButton b1 = new JButton("BACK");        b1.setBounds(400, 410, 130, 30);        b1.setBackground(Color.black);        b1.setForeground(Color.white);        panel.add(b1);        b1.addActionListener(e -> setVisible(false));        setUndecorated(true);        setSize(700, 500);        setLayout(null);        setLocation(350, 250);        setVisible(true);    }    public static void main(String[] args) {        new Department();    }}