package hospital.management.system;import net.proteanit.sql.DbUtils;import javax.swing.*;import java.awt.*;import java.sql.ResultSet;public class Employee_info extends JFrame {    Employee_info() {        JPanel panel = new JPanel();        panel.setBounds(5, 5, 990, 590);        panel.setBackground(new Color(109, 164, 170));        panel.setLayout(null);        add(panel);        JTable table = new JTable();        table.setFont(new Font("Tahoma", Font.PLAIN, 12));        table.setRowHeight(25);        JScrollPane scrollPane = new JScrollPane(table);        scrollPane.setBounds(10, 34, 970, 450);        panel.add(scrollPane);        try {            conn c = new conn();            String q = "select * from EMP_INFO";            ResultSet resultSet = c.statement.executeQuery(q);            table.setModel(DbUtils.resultSetToTableModel(resultSet));            table.getTableHeader().setFont(new Font("Tahoma", Font.BOLD, 14));            table.getTableHeader().setBackground(new Color(90, 156, 163));            table.getTableHeader().setForeground(Color.WHITE);        } catch (Exception e) {            e.printStackTrace();        }        JButton button = new JButton("BACK");        button.setBounds(420, 500, 120, 30);        button.setBackground(Color.BLACK);        button.setForeground(Color.white);        panel.add(button);        button.addActionListener(e -> setVisible(false));        setUndecorated(true);        setSize(1000, 600);        setLocation(350, 230);        setLayout(null);        setVisible(true);    }    public static void main(String[] args) {        new Employee_info();    }}