package hospital.management.system;import net.proteanit.sql.DbUtils;import javax.swing.*;import java.awt.*;import java.awt.event.ActionEvent;import java.awt.event.ActionListener;import java.sql.ResultSet;public class SearchRoom extends JFrame {    Choice choice;    JTable table;    SearchRoom() {        JPanel panel = new JPanel();        panel.setBounds(5, 5, 690, 490);        panel.setBackground(new Color(90, 156, 163));        panel.setLayout(null);        add(panel);        JLabel For = new JLabel("Search For Room");        For.setBounds(250, 11, 250, 31);        For.setForeground(Color.white);        For.setFont(new Font("Tahoma", Font.BOLD, 20));        panel.add(For);        JLabel status = new JLabel("Status :");        status.setBounds(70, 70, 80, 20);        status.setForeground(Color.white);        status.setFont(new Font("Tahoma", Font.BOLD, 14));        panel.add(status);        choice = new Choice();        choice.setBounds(170, 70, 120, 20);        choice.add("Available");        choice.add("Occupied");        panel.add(choice);        table = new JTable();        table.setRowHeight(25);        table.setFont(new Font("Tahoma", Font.PLAIN, 13));        JScrollPane scrollPane = new JScrollPane(table);        scrollPane.setBounds(0, 160, 690, 240);        panel.add(scrollPane);        try {            conn c = new conn();            String q = "select * from room";            ResultSet resultSet = c.statement.executeQuery(q);            table.setModel(DbUtils.resultSetToTableModel(resultSet));            table.getTableHeader().setFont(new Font("Tahoma", Font.BOLD, 14));            table.getTableHeader().setBackground(new Color(70, 130, 180));            table.getTableHeader().setForeground(Color.WHITE);        } catch (Exception e) {            e.printStackTrace();        }        JButton Search = new JButton("Search");        Search.setBounds(200, 420, 120, 25);        Search.setBackground(Color.black);        Search.setForeground(Color.white);        panel.add(Search);        Search.addActionListener(new ActionListener() {            @Override            public void actionPerformed(ActionEvent e) {                String q = "select * from room where Availability = '" + choice.getSelectedItem() + "'";                try {                    conn c = new conn();                    ResultSet resultSet = c.statement.executeQuery(q);                    table.setModel(DbUtils.resultSetToTableModel(resultSet));                } catch (Exception E) {                    E.printStackTrace();                }            }        });        JButton Back = new JButton("Back");        Back.setBounds(380, 420, 120, 25);        Back.setBackground(Color.black);        Back.setForeground(Color.white);        panel.add(Back);        Back.addActionListener(e -> setVisible(false));        setUndecorated(true);        setSize(700, 500);        setLayout(null);        setLocation(450, 250);        setVisible(true);    }    public static void main(String[] args) {        new SearchRoom();    }}